import React from 'react';
import bem from 'react-bem-helper';
import DomainVariations from 'config/domain-variations';
import { getAssetPath } from 'helpers';

const classes = new bem({
  name: 'header-logo',
  prefix: ''
});

export default React.createClass({

  displayName: 'HeaderLogo',

  propTypes: {
    id: React.PropTypes.string.isRequired,
    mobile: React.PropTypes.bool
  },

  logoPath(id) {
    let key = this.props.mobile ? 'logoMobile' : 'logo';
    let src;
    if (DomainVariations[id]) src = 'images/logos/' + DomainVariations[id][key];
    return src;
  },

  render() {
    return (
      <a {...classes()} href='/' title='Startseite'>
        <img {...classes('img')} src={getAssetPath(this.logoPath(this.props.id))} />
      </a>
    );
  }

});
