import React from 'react';

export default React.createClass({

  displayName: 'HeaderNav',

  propTypes: {
    isClosed: React.PropTypes.bool,
    id: React.PropTypes.number
  },

  getInitialState() {
    return {
      isHidden: true
    };
  },

  toggleNavigation() {
    this.setState({ isHidden: !this.state.isHidden });
  },

  render() {
    const { id } = this.props;

    const domainVariations = [
      {
        href: 'http://aargauerzeitung.ch',
        label: 'Aargau'
      },
      {
        href: 'http://bzbasel.ch',
        label: 'Basel'
      },
      {
        href: 'http://basellandschaftlichezeitung.ch',
        label: 'Basel'
      },
      {
        href: 'oltnertagblatt.ch',
        label: 'Solothurn'
      },
      {
        href: 'http://grenchnertagblatt.ch',
        label: 'Solothurn'
      },
      {
        href: 'http://solothurnerzeitung.ch ',
        label: 'Solothurn'
      },
      {
        href: 'http://limmattalerzeitung.ch',
        label: 'Limmattal'
      },
      {
        href: 'http://badenertagblatt.ch ',
        label: 'Aargau'
      }
    ];

    var navItems = [
      {
        href: '/',
        label: 'Start'
      },
      {
        href: '/',
        label: 'Aargau'
      },
      {
        href: '/',
        label: 'Schweiz'
      },
      {
        href: '/',
        label: 'Ausland'
      },
      {
        href: '/',
        label: 'Wirtschaft'
      },
      {
        href: '/',
        label: 'Sport'
      },
      {
        href: '/',
        label: 'Panorama'
      },
      {
        href: '/',
        label: 'Kultur'
      },
      {
        href: '/',
        label: 'Blaulicht'
      }
    ];

    if (id && domainVariations.length <= id) {
      navItems.splice(1, 0,
        {
          href: domainVariations[id].href,
          label: domainVariations[id].label
        }
      );
    }

    return (
      <nav className="header-nav">
        <div className="header-nav__brand">
          <button onClick={this.toggleNavigation} className="header-nav__menu-button">
            <img className="header-nav__menu-icon" src="/__node__/__static__/client/images/icons/menu.svg" />
          </button>
          <a href="/" title="Startseite">
            <img className="header-nav__logo header-nav__logo--notext" src="/__node__/__static__/client/images/az-nwch-logo-noname.svg" />
            <img className="header-nav__logo header-nav__logo--text" src="/__node__/__static__/client/images/az-nwch-logo-name.svg" />
          </a>
        </div>
        <nav className={"header-nav__list"+(this.state.isHidden ? ' header-nav__list--hidden' : '')}>
          <ul role='navigation'>
            <li>
              <a
                href="/login"
                className='header-nav__link header-nav__link--login'>
                Login
              </a>
            </li>
            {navItems.map((navItem, index) =>
              <li key={index}>
                <a
                  href={navItem.href}
                  className='header-nav__link'>
                  {navItem.label}
                </a>
              </li>
            )}
          </ul>
        </nav>
      </nav>
    );
  }

});
