/* eslint-disable no-unused-expressions */

import _ from 'lodash';
import slot from 'components/header-nav/slot';
import state from 'utils/data/master-article';

import { expect } from 'chai';

describe('HeaderNav', function() {

  describe('slot', function() {

    let fixture;

    beforeEach(function() {
      fixture = slot.data(state);
    });

    it('sets iso rendering', function() {
      expect(slot.iso).to.be.true;
    });

    it('returns data', function() {
      expect(fixture).to.be.an('object');
    });

    it('data has a skin ID', function() {
      expect(fixture.skin).to.be.a('string');
    });

    it('data has a nav array', function() {
      expect(fixture.navigation).to.be.an('array');
    });

    it('with the correct number of items', function() {
      expect(fixture.navigation.length).to.equal(9);
    });

    it('items contain a href / label', function() {
      let firstNavItem = _.first(fixture.navigation);
      expect(_.has(firstNavItem, 'href')).to.be.true;
      expect(_.has(firstNavItem, 'label')).to.be.true;
    });

    it('items show correct active state', function() {
      let targetNavItem = _.find(fixture.navigation, function(chr) {
        return chr.label === 'Blaulicht';
      });
      expect(_.get(targetNavItem, 'active')).to.be.true;
    });

  });

});
