import React from 'react';
import path from 'path';

export default React.createClass({

  displayName: 'Icon',

  propTypes: {
    id: React.PropTypes.string.isRequired
  },

  render() {
    var prefix = process.env.STATIC_ASSETS || '/__node__/__static__/client';
    var href = path.join(prefix, 'images/icons.svg#' + this.props.id);
    var useEl = '<use xlink:href="' + href + '" />';

    return (
      <svg className="icon" dangerouslySetInnerHTML={{ __html: useEl }} />
    );
  }

});
