import React from 'react';
import path from 'path';

export default React.createClass({

  displayName: 'Icon',

  propTypes: {
    id: React.PropTypes.string.isRequired,
    className: React.PropTypes.string
  },

  render() {
    var prefix = process.env.STATIC_ASSETS;
    var href = path.join(prefix, 'images/icons.svg#' + this.props.id);
    var useEl = '<use xlink:href="' + href + '" />';

    return (
      <svg className={this.props.className ? this.props.className : 'icon'} dangerouslySetInnerHTML={{ __html: useEl }} />
    );
  }

});
