import React from 'react';
import bem from 'react-bem-helper';

import Wrapper from './wrapper';

import ResponsiveImage from 'components/responsive-image';
import Caption from 'components/caption';

const classes = new bem({
  name: 'image-asset',
  prefix: ''
});

const ImageAsset = React.createClass({

  displayName: 'ImageAsset',

  propTypes: {
    image: React.PropTypes.object.isRequired,
    caption: React.PropTypes.object
  },

  render() {
    return (
      <figure itemScope itemType='http://schema.org/ImageObject' {...classes()}>
        <ResponsiveImage {...this.props.image} />
        <Caption {...this.props.caption} />
      </figure>
    );
  }

});

export default Wrapper(ImageAsset);
