import React from 'react';

import stripMarkup from 'helpers/strip-markup';

export default Component =>  React.createClass({

  displayName: 'ImageAssetWrapper',

  propTypes: {
    asset: React.PropTypes.object,
    title: React.PropTypes.string.isRequired,
    description: React.PropTypes.string,
    image_url: React.PropTypes.string,
    size: React.PropTypes.string
  },

  mappedData(props) {

    var asset = props.asset;

    var description = props.description
      ? stripMarkup(props.description)
      : asset.description;

    return {
      size: props.size,
      src: props.image_url || asset.image_url,
      alt: props.title || asset.title,
      title: description,
      caption: props.title || asset.title,
      description: description,
      source: asset.source.title || (asset.source.source ? asset.source.source.title : null)
    };

  },

  render() {
    return (
      <Component {...this.mappedData(this.props)} />
    );
  }

});
