import React from 'react';

export default Component =>  React.createClass({

  displayName: 'ImageGalleryWrapper',

  propTypes: {
    title: React.PropTypes.string,
    description: React.PropTypes.string,
    asset: React.PropTypes.object.isRequired
  },

  mappedImages(images) {
    return images.map(function(image) {
      return {
        title: image.title || image.asset.title,
        description: image.description || image.asset.description,
        source: image.asset.source.title || (image.asset.source.source ? image.asset.source.source.title : false),
        url: image.image_url
      };
    });
  },

  mappedData(props) {
    return {
      title: props.title || props.asset.title,
      description: props.description || props.asset.description,
      images: this.mappedImages(props.asset.images)
    };
  },

  render() {
    return (
      <Component {...this.mappedData(this.props)} />
    );
  }
});
