import React from 'react';
import bem from 'react-bem-helper';
import _ from 'lodash';

import Container from 'components/container';
import SectionHeader from 'components/section-header';
import TabbedNavigation from 'components/tabbed-navigation';
import LatestArticlesSection from 'components/latest-articles-section';

import { getViewportWidth } from 'helpers';

const classes = new bem({
  name: 'latest-articles',
  prefix: ''
});

const sections = [
  {
    id: 'latest',
    icon: 'bullet',
    title: 'Neueste',
    detailedLabel: 'Meldungen'
  },
  {
    id: 'popular',
    icon: 'views',
    title: 'Popular',
    detailedLabel: 'Meldungen'
  }
];

export default React.createClass({

  propTypes: {
    latest: React.PropTypes.arrayOf(React.PropTypes.object).isRequired,
    popular: React.PropTypes.arrayOf(React.PropTypes.object).isRequired,
    showingId: React.PropTypes.string
  },

  displayName: 'LatestArticles',

  breakpoint: 900,

  itemCount: {
    mobile: 5,
    desktop: 9
  },

  getCurrentView() {
    return getViewportWidth() > this.breakpoint
      ? 'desktop'
      : 'mobile';
  },

  getArticlesForView(showingId, viewName) {
    let allArticles = this.props[showingId];
    return allArticles.slice(0, this.itemCount[viewName || this.getCurrentView()]);
  },

  showSection(id) {
    this.setState({
      showingId: id,
      articles: this.getArticlesForView(id),
    });
  },

  handleResize() {
    if (this.getCurrentView() !== this.state.view) {
      this.setState({
        articles: this.getArticlesForView(this.state.showingId),
        view: this.getCurrentView(),
        loading: false
      });
    } else if (this.state.loading) {
      this.setState({ loading: false });
    }
  },

  getInitialState() {
    // get our initially shown tab
    let initialShowingId = this.props.showingId || _.first(sections).id;
    // set initial DOM data
    return {
      showingId: initialShowingId,
      articles: this.getArticlesForView(initialShowingId, 'mobile'),
      loading: true,
      view: 'mobile'
    };
  },

  componentDidMount() {
    this.handleResize();
    this.resizeEvent = _.debounce(this.handleResize, 150);
    window.addEventListener('resize', this.resizeEvent);
  },

  componentWillUnmount() {
    window.removeEventListener('resize', this.resizeEvent);
  },

  render() {

    let { showingId, articles, loading } = this.state;

    return (
      <section {...classes(null, null, [ loading ? 'is-loading' : null])}>
        <Container>
          <SectionHeader title='Halten Sie sich auf dem Laufenden' />
          <TabbedNavigation
            tabs={sections}
            showing={showingId}
            handleSelect={this.showSection}
          />
          <div {...classes('showing')}>
            <LatestArticlesSection items={articles} />
          </div>
        </Container>
      </section>
    );

  }

});
