import React from 'react';
import bem from 'react-bem-helper';

import Enrichment from 'components/enrichment';
import GetEnrichment from 'higher-order/get-enrichment';

const classes = new bem({
  name: 'main-asset',
  prefix: ''
});

export default React.createClass({

  displayName: 'MainAsset',

  propTypes: {
    asset: React.PropTypes.object
  },

  render() {
    var type = this.props.asset.asset.content_type;

    return (
      <div {...classes()}>
        <Enrichment type={type}>
          <GetEnrichment enrichment={this.props.asset} />
        </Enrichment>
      </div>
    );
  }

});
