import React from 'react';
import bem from 'react-bem-helper';

import Enrichment from 'components/enrichment';

import GetEnrichment from 'helpers/enrichment/get-enrichment';

import { getEnrichmentType } from 'helpers/enrichment';

const classes = new bem({
  name: 'main-asset',
  prefix: ''
});

export default React.createClass({

  displayName: 'MainAsset',

  propTypes: {
    asset: React.PropTypes.object
  },

  render() {
    const { asset } = this.props;
    return (
      <div {...classes()}>
        <Enrichment type={getEnrichmentType(asset)}>
          <GetEnrichment enrichment={asset} />
        </Enrichment>
      </div>
    );
  }

});
