import React from 'react';
import bem from 'react-bem-helper';

import Button from 'components/button';

const classes = new bem({
  name: 'media-buttons',
  prefix: ''
});

export default React.createClass({

  print() {
    if (typeof window !== 'undefined') {
      window.print();
    }
  },

  render() {

    return (
      <div {...classes()}>
        <Button icon='facebook' target='#' />
        <Button icon='twitter' target='#' />
        <Button icon='mail' target='#' />
        <Button icon='print' target={this.print} />
      </div>
    );

  }

});
