import React from 'react';
import bem from 'react-bem-helper';

import Button from 'components/button';
import SearchInput from 'components/search-input';

const classes = new bem({
  name: 'meta-nav',
  prefix: ''
});

export default React.createClass({

  displayName: 'MetaNav',

  showAlert() {
    /* eslint-disable no-alert */
    window.alert('Leider wurde dieses Feature noch nicht implementiert.');
  },

  render() {

    return (
      <div {...classes()}>
        <div {...classes('container')}>
          <ul {...classes('nav')}>
            <li><a {...classes('item')} href="/login">Abo</a></li>
            <li><a {...classes('item')} href="/login">E-Paper</a></li>
            <li><a {...classes('item')} href="/login">Werbung</a></li>
            <li><a {...classes('item')} href="/login">Todesanzeigen</a></li>
          </ul>
          <SearchInput />
          <Button icon='user' _event={this.showAlert} label="Login" modifier="meta" attributes={{'title': 'Login'}} />
        </div>
      </div>
    );
  }

});
