import React from 'react';
import moment from 'moment';

import Icon from 'components/icon';

export default React.createClass({

  displayName: 'PlayTime',

  propTypes: {
    duration: React.PropTypes.number.isRequired
  },

  getFormattedTime(duration) {
    var time;

    time = moment.utc(duration * 1000).format('mm:ss');
    time= (duration ? time : '');

    return time;
  },

  getIsoTime(duration) {
    return moment.utc(duration * 1000).format('HH:mm:ss');
  },

  render() {
    var playtime = this.getFormattedTime(this.props.duration);
    var isoTime = this.getIsoTime(this.props.duration);

    return (
      <div className="play-time"><Icon id="play" />
        {playtime}
        <meta itemProp="duration" content={isoTime}/>
      </div>
    );
  }
});
