import React from 'react/addons';
const TestUtils = React.addons.TestUtils;

import PlayTime from 'components/play-time';
import data from 'components/play-time/.data';

import { expect } from 'chai';

describe('Play Time\'s', function() {
  var playTime;

  beforeEach(function() {
    playTime = TestUtils.renderIntoDocument(
      <PlayTime {...data} />
    );
  });

  describe('getFormattedTime() takes a duration in seconds', function() {
    it('and returns a time in format mm:ss', () => {
      expect(playTime.getFormattedTime(99)).to.equal('01:39');
    });

    it('and returns an empty string if duration is zero', () => {
      expect(playTime.getFormattedTime(0)).to.equal('');
    });
  });

  describe('getIsoTime() takes a duration in seconds', function() {
    it('and returns a time in format hh:mm:ss', () => {
      expect(playTime.getFormattedTime(3662)).to.equal('01:01:02');
    });

    it('and returns 00:00:00 if duration is zero', () => {
      expect(playTime.getFormattedTime(0)).to.equal('00:00:00');
    });
  });

});
