import React from 'react';
import { cleanContent } from 'helpers';

export default React.createClass({

  displayName: 'RawContent',

  propTypes: {
    content: React.PropTypes.string.isRequired
  },

  render() {
    // ensure we always open with a div for styling root text nodes
    const contentWrapped = '<div>' + this.props.content + '</div>';
    return (
      <div className="copy" dangerouslySetInnerHTML={{ __html: cleanContent(contentWrapped) }} />
    );
  }

});
