import React from 'react';

export default React.createClass({

  displayName: 'RawContent',

  propTypes: {
    content: React.PropTypes.string.isRequired
  },

  // Clean crap from CMS
  purgeContent(text) {
    return text.replace(/<p>(\s|&nbsp;)*<\/p>/g, '');
  },

  render() {
    return (
      <div className="copy" dangerouslySetInnerHTML={{ __html: this.purgeContent(this.props.content) }} />
    );
  }

});
