import React from 'react';
import bem from 'react-bem-helper';

import Teaser from 'components/teaser';

const classes = new bem({
  name: 'recommendations-bottom',
  prefix: ''
});

export default React.createClass({

  variations: ['large', 'light', 'light', 'basic', 'basic', 'basic'],

  propTypes: {
    teasers: React.PropTypes.array.isRequired
  },

  render() {

    return (

      <div {...classes()}>
        <p {...classes('catchword')}>Und Jetzt</p>
        <h3 {...classes('title')}>Lesen Sie Ausserdem</h3>
        <div {...classes('grid')}>
          {this.props.teasers.map((teaser, index) => {
            return (<div {...classes('cell', this.variations[index])}>
              <Teaser key={index} {...teaser} />
            </div>);
          })}
        </div>
      </div>

    );

  }

});
