import React from 'react';
import bem from 'react-bem-helper';

import Teaser from 'components/teaser';
import Icon from 'components/icon';

const classes = new bem({
  name: 'recommendations-top',
  prefix: ''
});

export default React.createClass({

  displayName: 'RecommendationsTop',

  propTypes: {
    teasers: React.PropTypes.array.isRequired
  },

  getInitialState() {
    return {
      mode: 'collapsed'
    };
  },

  expand() {
    this.setState({
      mode: 'expanded'
    });
  },

  render() {

    return (

      <div {...classes(null, this.state.mode)}>
        <h3 {...classes('title')}>Artikel zum Thema</h3>
        <div {...classes('grid')}>
          <div {...classes('cell', 'inlined-right')} key={'cell' + 0}>
            <Teaser key={'cell' + 0} {...this.props.teasers[0]} />
          </div>
          <button onClick={this.expand} {...classes('button')}>Mehr artikel zum thema <Icon id='download' /></button>
          <div {...classes('cell', 'inlined-right')} key={'cell' + 1}>
            <Teaser key={'cell' + 1} {...this.props.teasers[1]} />
          </div>
          <div {...classes('cell', 'inlined-right')} key={'cell' + 2}>
            <Teaser key={'cell' + 2} {...this.props.teasers[2]} />
          </div>
          <div {...classes('cell', 'inlined-right')} key={'cell' + 3}>
            <Teaser key={'cell' + 3} {...this.props.teasers[3]} />
          </div>
          <div {...classes('cell', 'inlined-right')} key={'cell' + 4}>
            <Teaser key={'cell' + 4} {...this.props.teasers[4]} />
          </div>
          <div {...classes('cell', 'inlined-right')} key={'cell' + 5}>
            <Teaser key={'cell' + 5} {...this.props.teasers[5]} />
          </div>
        </div>
      </div>

    );

  }

});
