import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'related-videos',
  prefix: ''
});

export default React.createClass({

  render() {
    return (
      <div {...classes()}>
        <h4 className="type-h4">Verwandte Videos</h4>
        <div className="veeseoRA2VW"></div>
        <script src="http://rce.veeseo.com/widgets/aargauerzeitung/widget.js"></script>
        <ul>
          {this.props.videos.map(function(video) {
            return (
              <li key={video.title}>
                <img src={video.image_url} />
                <span {...classes('caption')}>
                  <svg {...classes('icon')} dangerouslySetInnerHTML={{ __html: "<use xlink:href='/client/images/icons.svg#play' />" }} /> <span {...classes('duration')}>{video.length}</span><br />
                  <span {...classes('title')}>{video.title}</span>
                </span>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
});
