import React from 'react';
import BEM from 'react-bem-helper';

import KalturaImageProxy from 'helpers/kaltura-image-proxy';

const classes = new BEM({
  name: 'responsive-video-image',
  prefix: ''
});

export default React.createClass({

  displayName: 'ResponsiveVideoImage',

  propTypes: {
    accountID: React.PropTypes.string.isRequired,
    videoID: React.PropTypes.string.isRequired,
    title: React.PropTypes.string,
    src: React.PropTypes.string.isRequired,
    alt: React.PropTypes.string,
    ssl: React.PropTypes.bool.isRequired,
  },

  render() {
    var { src, title, alt } = this.props;

    var proxy = new KalturaImageProxy(this.props.accountID, false, this.props.videoID);
    src = proxy.getImageURL(350, 197, true);

    return (
      <div {...classes()}>
        <img
          {...classes('img')}
          src={src}
          alt={alt}
          title={title}
        />
      </div>
    );
  }
});
