import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'ressort-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'RessortHeader',

  propTypes: {
    title: React.PropTypes.string.isRequired,
    slug: React.PropTypes.string.isRequired,
    subnav: React.PropTypes.arrayOf(React.PropTypes.object).isRequired
  },

  subRessortSlug(subressort) {
    return '/' + this.props.slug + '/' + subressort;
  },

  render() {

    var { title, subnav } = this.props;

    return (
      <div {...classes()}>
        <h1 {...classes('title')}>{title}</h1>
        <ul {...classes('subnav')}>
          {subnav.map((item, index) => {
            return (
              <li {...classes('subnav-item')} key={index}>
                <a href={this.subRessortSlug(item.slug)} key={index}>
                  {item.title}
                </a>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }

});
