import React from 'react';
import bem from 'react-bem-helper';

import SecondaryNav from 'components/secondary-nav';

const classes = new bem({
  name: 'ressort-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'RessortHeader',

  propTypes: {
    title: React.PropTypes.string.isRequired,
    navigation: React.PropTypes.array.isRequired
  },

  render() {
    const { title, navigation } = this.props;
    let hasNav = navigation && navigation.length;
    return (
      <div {...classes(null, [hasNav ? 'subressorts' : null])}>
        <h1 {...classes('title')}>{title}</h1>
        {hasNav
          ? <SecondaryNav items={navigation} />
        :null}
      </div>
    );
  }

});
