var parsedRessort = function(ressort, subressorts) {
  return {
    title: ressort.title,
    urlpart: ressort.urlpart,
    subnav: subressorts ? subressorts : false
  };
};

var parsedData = function(state) {
  return parsedRessort(state.ressort, state.subressorts);
};

module.exports = {
  iso: false,
  data: parsedData
};
