module.exports = function(state) {

  var parsedData = function(ressort, subressorts) {
    if (ressort && subressorts && subressorts.length > 0) {
      return {
        hasNav: true,
        title: ressort.title,
        urlpart: ressort.urlpart,
        subnav: subressorts
      };
    }
    return {
      hasNav: false
    };
  };

  return parsedData(state.ressort, state.subressorts);

};
