module.exports = function(state) {

  var parsedData = function(ressort, subressorts) {
    return {
      title: ressort.title,
      urlpart: ressort.urlpart,
      subnav: subressorts ? subressorts : false
    };
  };

  return {
    iso: true,
    data: parsedData(state.ressort, state.subressorts)
  };

};
