import React from 'react';
import bem from 'react-bem-helper';

import Grid from 'components/grid';
import Container from 'components/container';

import RessortTeaserBuilder from 'higher-order/ressort-teaser-builder';

const classes = new bem({
  name: 'ressort-teaser',
  prefix: ''
});

var RessortTeaser = React.createClass({

  displayName: 'Ressort Teaser',

  propTypes: {
    title: React.PropTypes.string,
    ressortUrl: React.PropTypes.string,
    items: React.PropTypes.array,
    modifier: React.PropTypes.string
  },

  render() {

    const { title, ressortUrl, items, modifier } = this.props;

    return (
      <section {...classes()}>
        <Container>
          {title ?
            <header {...classes('header')}>
                <h1 {...classes('heading')}>
                  <a href={ ressortUrl } {...classes('heading-link')}>
                    { title }
                  </a>
                </h1>
            </header>
          : null}
          <Grid modifier={ modifier } items={ items }/>
          <footer {...classes('footer')}>
            {ressortUrl && title ?
              <a href={ ressortUrl } {...classes('cta')}>Mehr artikel in { title } &#10142;</a>
            : null}
            <div {...classes('rule')} />
          </footer>
        </Container>
      </section>
    );
  }

});

export default RessortTeaserBuilder(RessortTeaser);
