'use strict';

const React = require('react');

const SampleBar = React.createClass({

  propTypes: {
    title: React.PropTypes.string,
    description: React.PropTypes.string
  },

  render() {
    return (
      <div>
        <h4>{this.props.title}</h4>
        <p>{this.props.description}</p>
      </div>
    );
  }

});

module.exports = SampleBar;
