import Post from './index.jsx';
import createComponent from 'utils/test/create-component';

import { expect } from 'chai';

describe('Post component', function() {
  var post;

  beforeEach(function() {
    post = createComponent(Post, {
      title: 'Title',
      description: 'Description'
    });
  });

  describe('has content', function() {
    it('title', function() {
      const postTitle = post.props.children[0];
      expect(postTitle.props.children).to.equal('Title');
    });

    it('description', function() {
      const postContent = post.props.children[1];
      expect(postContent.props.children).to.equal('Description');
    });
  });

});
