import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'search-input',
  prefix: ''
});

export default React.createClass({

  displayName: 'SearchInput',

  showAlert() {
    /* eslint-disable no-alert */
    window.alert('Leider wurde dieses Feature noch nicht implementiert.');
  },

  render() {

    return (
      <div {...classes()}>
        <form>
          <input type="text" name="input" {...classes('input')} placeholder="Suchen" onClick={this.showAlert} />
          <input type="submit" name="submit" {...classes('submit')} value="Suchen" />
        </form>
      </div>
    );
  }

});
