import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'section-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'SectionHeader',

  propTypes: {
    title: React.PropTypes.string.isRequired,
    subtitle: React.PropTypes.string,
    link: React.PropTypes.string,
    catchword: React.PropTypes.string,
    modifier: React.PropTypes.string,
    titleModifier: React.PropTypes.string
  },

  render() {
    const { title, link, catchword, modifier, titleModifier, subtitle } = this.props;
    return (
      <header {...classes(null, [modifier ? modifier : 'default'])}>
        {catchword
          ? <p {...classes('catchword')}>{catchword}</p>
        :null}
        <h1 {...classes('title', [titleModifier || 'default'])}>
          {link
            ? <a href={link} {...classes('link')}>{title}</a>
            : <span>{title}</span>
          }
        </h1>
        { subtitle
          ? <p {...classes('subtitle')}>
              { subtitle }
            </p>
          : null
        }

      </header>
    );
  }

});
