'use strict';

const React = require('react');
const bem = require('react-bem-helper');

const classes = new bem({
  name: 'stoerer',
  prefix: ''
});

const Stoerer = React.createClass({

  render() {
    var position = ['left', 'right'].indexOf(this.props.placement) > -1 ? 'floating' : 'full';
    return (
      <div {...classes(null, position)}>
        <h2 {...classes('title')}>{this.props.title}</h2>
        <div {...classes('body')} dangerouslySetInnerHTML={{ __html: this.props.text }} />
      </div>
    );
  }

});

module.exports = Stoerer;
