'use strict';

const React = require('react');
const bem = require('react-bem-helper');

const classes = new bem({
  name: 'tags',
  prefix: ''
});

const Tags = React.createClass({

  propTypes: {
    keywords: React.PropTypes.string.isRequired
  },

  keywordsify(value) {
    return value.split(',').map(function(item) {
      return item.replace(/^\s/, '').replace(/\s$/, '');
    });
  },

  render() {
    return (
      <div {...classes()}>
        <p {...classes('title')}>Tags</p>
        <ul {...classes('list')}>
        {this.keywordsify(this.props.keywords).map(function(keyword) {
          return <li {...classes('item')}><a href="#" {...classes('link')}>{keyword}</a></li>;
        })}
        </ul>
      </div>
    );
  }

});

module.exports = Tags;
