import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'tags',
  prefix: ''
});

export default React.createClass({

  propTypes: {
    keywords: React.PropTypes.string.isRequired
  },

  keywordsify(value) {
    return value.split(',').map(function(item) {
      return item.replace(/^\s/, '').replace(/\s$/, '');
    });
  },

  render() {
    return (
      <div {...classes()}>
        <p {...classes('title')}>Tags</p>
        <ul {...classes('list')}>
        {this.keywordsify(this.props.keywords).map(function(keyword, index) {
          return <li key={index} {...classes('item')}><a href="#" {...classes('link')}>{keyword}</a></li>;
        })}
        </ul>
      </div>
    );
  }

});
