import React from 'react';
import bem from 'react-bem-helper';

import TeaserMarker from 'components/teaser-marker';
import ResponsiveImage from 'components/responsive-image';

const classes = new bem({
  name: 'teaser-image',
  prefix: ''
});

export default React.createClass({

  displayName: 'TeaserImage',

  propTypes: {
    image: React.PropTypes.object,
    marker: React.PropTypes.object,
    modifiers: React.PropTypes.array
  },

  render() {
    const { image, marker, modifiers } = this.props;

    if (!image || image && !image.src || !image.size) {
      return null;
    }

    return (
      <div {...classes(null, modifiers)} itemProp='image' itemScope itemType='http://schema.org/ImageObject'>
        <TeaserMarker
          {...marker}
          modifiers={modifiers}
        />
        <ResponsiveImage
          {...image}
          fill={true}
          fitToBox={true}
        />
      </div>
    );
  }
});
