import React from 'react';

export default Component => React.createClass({

  displayName: 'TeaserWrapper',

  propTypes: {
    article: React.PropTypes.object.isRequired,
    variation: React.PropTypes.string.isRequired
  },

  mapDateString(article, variation) {
    return variation ? article.dc.created : '';
  },

  mappedData(props) {
    var article = props.article;

    return {
      modifier: props.variation,
      title: article.title,
      catchword: article.context_label,
      supplementary: this.mapDateString(article, props.variation),
      asset: (props.variation === 'light' || props.variation === 'light-boxed' ? null : article.assets[0])
    };
  },

  render() {
    return (
      <Component {...this.mappedData(this.props)} />
    );
  }

});
