import React from 'react';
import bem from 'react-bem-helper';
import { capitalize } from 'lodash/string';

import SectionHeader from 'components/section-header';
import Container from 'components/container';
import TopicBody from 'components/topic-body';

const classes = new bem({
  name: 'topic-detail',
  prefix: ''
});

export default React.createClass({

  displayName: 'TopicDetail',

  propTypes: {
    topic: React.PropTypes.string.isRequired,
    articles: React.PropTypes.arrayOf(React.PropTypes.object).isRequired,
    page: React.PropTypes.number.isRequired,
    total: React.PropTypes.number.isRequired
  },

  render() {
    return (
      <div {...classes()}>
        <Container>
          <SectionHeader title={capitalize(this.props.topic)} catchword='Thema' titleModifier='large' />
          <TopicBody {...this.props} />
        </Container>
      </div>
    );
  }

});
