/* eslint-disable no-unused-expressions */

import slot from 'components/topic-detail/slot';
import state from 'utils/data/master-topic';

import { expect } from 'chai';

describe('TopicDetail', function() {

  describe('slot', function() {

    let fixture;

    beforeEach(function() {
      fixture = slot.data(state);
    });

    it('sets iso rendering', function() {
      expect(slot.iso).to.be.true;
    });

    it('returns data', function() {
      expect(fixture).to.be.an('object');
    });

    it('data has articles', function() {
      expect(fixture.articles).to.be.an('array');
    });

    it('with the correct number of items', function() {
      expect(fixture.articles.length).to.equal(4);
    });

    it('data contains a total', function() {
      expect(fixture.total).to.be.a('number');
    });

    it('data contains a page id', function() {
      expect(fixture.page).to.be.a('number');
    });

    it('articles total equals total count', function() {
      expect(fixture.articles.length).to.equal(fixture.total);
    });

  });

});
