import React from 'react';
import bem from 'react-bem-helper';

import slugToTitle from 'slug-to-title';

const classes = new bem({
  name: 'topic-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'TopicHeader',

  propTypes: {
    topic: React.PropTypes.string.isRequired,
  },

  render() {
    var { topic } = this.props;
    var title = topic[0].toUpperCase() + topic.substring(1);

    return (
      <header {...classes()}>
        <p {...classes('catchword')}>Thema</p>
        <h1 {...classes('title')}>{slugToTitle(title)}</h1>
      </header>
    );
  }

});
