const Typography = React.createClass({

  render: function() {
    return (
      <div>
        {data.map((section, index) =>
          <TypographyItem key={index} {...section.props}>
            {section.content}
          </TypographyItem>
        )}
      </div>
    );
  }

});

module.exports = Typography;
