import React from 'react';
import bem from 'react-bem-helper';

const classes = new bem({
  name: 'video-library-filter',
  prefix: ''
});

export default React.createClass({

  displayName: 'VideoLibraryFilter',

  propTypes: {
    newest: "Aktuellste",
    popular: "Beliebteste"
  },

  handleChange(event) {
    var value = event.target.value;
    this.props.newest(value);
    this.props.popular(value);
  },

  render() {
    return (
      <div {...classes()}>
        <label {...classes('newest', null, 'is-active')}>
          <input type="radio" onChange={this.handleChange} name="newest" value={this.props.newest} />
          Aktuellste
        </label>
        <label {...classes('popular')}>
          <input type="radio" onChange={this.handleChange} name="popular" value={this.props.newest} />
          Beliebteste
        </label>
      </div>
    );
  }

});
