import React from 'react';
import bem from 'react-bem-helper';

import Icon from 'components/icon';

const classes = new bem({
  name: 'video-library-filter',
  prefix: ''
});

export default React.createClass({

  displayName: 'VideoLibraryFilter',

  propTypes: {
    newest: "Aktuellste",
    popular: "Beliebteste"
  },

  handleChange(event) {
    var value = event.target.value;
    this.props.newest(value);
    this.props.popular(value);
  },

  render() {
    return (
      <div {...classes()}>
        <a href='#' {...classes('newest', null, 'is-active')}><Icon id="clock"/> Aktuellste</a>
        <a href='#' {...classes('popular')}><Icon id="heart"/> Beliebteste</a>
      </div>
    );
  }

});
