import React from 'react';
import BEM from 'react-bem-helper';

import Icon from 'components/icon';

const classes = new BEM({
  name: 'video-library-filter',
  prefix: ''
});

export default React.createClass({

  displayName: 'VideoLibraryFilter',

  render() {
    return (
      <div {...classes()}>
        <a href="#" {...classes('newest', null, 'is-active')}>
          <Icon id="clock" /> Aktuell
        </a>
        <a href="#" {...classes('popular')}>
          <Icon id="heart" /> Beliebteste
        </a>
      </div>
    );
  }

});
