import VideoLibraryFilter from 'components/video-library-filter';
import React from 'react/addons';
import { expect } from 'chai';

describe('VideoLibraryFilter', function() {

  describe('renders newest and popular Filter', function() {
    var videofilter;

    it('shows the Filter "Aktuell"', () => {
      videofilter  = React.renderToString(React.createElement(VideoLibraryFilter));
      expect(videofilter).to.contain(' Aktuell');
    });

    it('shows the Filter "Beliebteste"', () => {
      videofilter  = React.renderToString(React.createElement(VideoLibraryFilter));
      expect(videofilter).to.contain(' Beliebteste');
    });

  });
});
