import React from 'react';
import bem from 'react-bem-helper';

import VideoLibrarySearch from 'components/video-library-search';

const classes = new bem({
  name: 'video-library-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'VideoLibraryHeader',

  render() {
    var page = 'Neuste Videos';
    var title = page.toUpperCase();

    return (
      <div {...classes('container')}>
        <h1 {...classes('title')}>{title}</h1>
        <VideoLibrarySearch {...classes('search')} searchText={this.props.searchText} changeSearchText={this.props.changeSearchText}/>
      </div>
    );
  }

});
