import React from 'react';
import BEM from 'react-bem-helper';

import VideoLibrarySearch from 'components/video-library-search';

const classes = new BEM({
  name: 'video-library-header',
  prefix: ''
});

export default React.createClass({

  displayName: 'VideoLibraryHeader',

  propTypes: {
    onChangeSearchText: React.PropTypes.func.isRequired,
    onSearch: React.PropTypes.func.isRequired
  },

  render() {
    var title = 'Neuste Videos';

    return (
      <div {...classes('container')}>
        <h1 {...classes('title')}>{title}</h1>
        <VideoLibrarySearch
          {...classes('search')}
          onChangeSearchText={this.props.onChangeSearchText}
          onSearch={this.props.onSearch}
        />
      </div>
    );
  }
});
