/*eslint-disable no-console, no-unused-expressions */
import React from 'react/addons';
const TestUtils = React.addons.TestUtils;

import { expect } from 'chai';

import VideoLibraryHeader from 'components/video-library-header';



describe('Video Library Header', function() {
  var reactElement;
  var component;
  var componentWithClass;
  var dummyComponent;

  beforeEach(function() {
    reactElement = <VideoLibraryHeader />;
    component = TestUtils.renderIntoDocument(reactElement);
    componentWithClass = TestUtils.findRenderedDOMComponentWithClass(component, 'video-library-header');
  });

  describe('renders', function() {
    it('Dummy element as a react element', function() {
      expect(TestUtils.isElement( React.createElement('div') )).to.be.true;
    });

    it('a react element', function() {
      expect(TestUtils.isElement(reactElement)).to.be.true;
    });

    it('Dummy as a react component', function() {
      dummyComponent = TestUtils.renderIntoDocument(React.createElement('div'));
      expect(TestUtils.isDOMComponent(dummyComponent)).to.be.true;
    });

    // @TODO:@Richard: Any idea why this is failing?
    it('a DOM Component', function() {
      expect(TestUtils.isDOMComponent(component)).to.be.true;
    });

    it('a DOM Component with a BEM class', function() {
      expect(TestUtils.isDOMComponent(componentWithClass)).to.be.true;
    });
  });
});
