import React from 'react';
import bem from 'react-bem-helper';

import Button from 'components/button';

const classes = new bem({
  name: 'video-library-search',
  prefix: ''
});

export default React.createClass({

  displayName: 'VideoLibrarySearch',

  propTypes: {
    searchText: React.PropTypes.string.isRequired,
    onSearch: React.PropTypes.func.isRequired
  },

  handleChange(event) {
    var value = event.target.value;
    this.props.changeSearchText(value);
  },

  handleEnter(e) {
    if (e.keyCode === 13) {
      this.props.onSearch();
    }
  },

  render() {
    return (
      <div {...classes('container')}>
        <input {...classes('textfield')} value={this.props.searchText} onChange={this.handleChange} onKeyDown={this.handleEnter}/><Button modifier="filter" icon="search" _event={this.props.onSearch} _target="#"/>
      </div>
    );
  }

});
