/*eslint-disable no-unused-expressions */
import React from 'react/addons';
const TestUtils = React.addons.TestUtils;
import { expect } from 'chai';

import VideoLibraryViewer from 'components/video-library-viewer';
import data from 'components/video-library-viewer/.data';
import Video from 'components/video';

describe('Video Library Viewer', function() {
  var reactElement;
  var component;

  beforeEach(function() {
    reactElement = <VideoLibraryViewer {...data} />;
    component = TestUtils.renderIntoDocument(reactElement);
  });

  describe('renders', function() {

    it('a DOM Component with a BEM class', () => {
      var componentWithClass = TestUtils.findRenderedDOMComponentWithClass(component, 'video-library-viewer');
      expect(TestUtils.isDOMComponent(componentWithClass)).to.be.true;
    });

    it('a Video Component', () => {
      expect(TestUtils.isCompositeComponent(TestUtils.findRenderedComponentWithType(component, Video))).to.be.true;
    });

  });
});
