import React from 'react/addons';
const TestUtils = React.addons.TestUtils;

import VideoTeaser from 'components/video-teaser';
import data from 'components/video-teaser/.data';

import { expect } from 'chai';

describe('Video Teaser', function() {
  var element;

  beforeEach(function() {
    element = TestUtils.renderIntoDocument(
      <VideoTeaser {...data} />
    );
  });

  describe('trims Title', function() {
    var title = data.name;

    it('to a length of 14 characters', () => {
      expect(element.trimTitle(title, 14)).to.equal('US-Unternehmer');
    });

  });

});
